sql:
drop table olap_product if exists;
drop table olap_region if exists;
drop table olap_time if exists;
drop table olap_sales if exists;

create cached table olap_product (id integer, product_line varchar, product_name varchar);
create cached table olap_region (id integer, zone_name varchar, territory_name varchar);
create cached table olap_time (id integer, year varchar, quarter varchar);
create cached table olap_sales (product_id integer, region_id integer, time_id integer, price integer);

